import React, { useState } from 'react';
import Tesseract from 'tesseract.js';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [recognizedText, setRecognizedText] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      setIsProcessing(true);
      setRecognizedText('');

      try {
        const result = await Tesseract.recognize(
          file,
          'chi_tra',
          {
            logger: info => {
              if (info.status === 'recognizing text') {
                console.log(info.progress);
              }
            }
          }
        );

        setRecognizedText(result.data.text);
      } catch (error) {
        console.error('识别错误:', error);
        setRecognizedText('识别出错，请重试');
      } finally {
        setIsProcessing(false);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <h1 className="text-3xl font-bold text-center mb-8">繁体字练字帖生成器</h1>
      <div className="max-w-2xl mx-auto space-y-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <input 
            type="file" 
            className="w-full p-2 border rounded"
            onChange={handleImageUpload}
            accept="image/*"
          />
          <p className="mt-2 text-sm text-gray-600">
            支持的图片格式：JPG、PNG、BMP等
          </p>
        </div>
        
        {selectedImage && (
          <div className="bg-white p-4 rounded-lg shadow">
            <img 
              src={selectedImage} 
              alt="Selected" 
              className="max-w-full rounded"
              style={{ maxHeight: '400px', objectFit: 'contain' }}
            />
          </div>
        )}
        
        {isProcessing && (
          <div className="text-center text-gray-600 bg-white p-4 rounded-lg shadow">
            <p>正在识别文字，请稍候...</p>
            <p className="text-sm mt-2">（首次识别可能需要下载语言包，请耐心等待）</p>
          </div>
        )}
        
        {recognizedText && (
          <div className="bg-white p-4 rounded-lg shadow">
            <h2 className="text-xl font-bold mb-2">识别结果：</h2>
            <textarea
              className="w-full h-48 p-2 border rounded"
              value={recognizedText}
              onChange={(e) => setRecognizedText(e.target.value)}
              placeholder="识别结果将显示在这里..."
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
