import React, { useState } from 'react';
import { createWorker } from 'tesseract.js';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [recognizedText, setRecognizedText] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [originalText, setOriginalText] = useState('');

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      await recognizeText(file);
    }
  };

  const recognizeText = async (file) => {
    setIsProcessing(true);
    try {
      const worker = await createWorker({
        logger: m => console.log(m),
        langPath: 'https://tessdata.projectnaptha.com/4.0.0',
        gzip: false,
      });
      
      await worker.loadLanguage('chi_tra');
      await worker.initialize('chi_tra');
      await worker.setParameters({
        tessedit_char_whitelist: '的一是不了人我在有他这为之大来以个中上们到说国和地也子时道出而要于就下得可你年生自会那后能对着事其里所去行过家十用发天如然作方成者多日都三小军二无同么经法当起与好看学进种将还分此心前面又门开但因什只从想实日军',
        preserve_interword_spaces: '1',
        tessedit_pageseg_mode: '1',
      });

      const { data: { text } } = await worker.recognize(file);
      setRecognizedText(text);
      await worker.terminate();
    } catch (error) {
      console.error('OCR识别错误:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleTextEdit = (e) => {
    setRecognizedText(e.target.value);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <h1 className="text-3xl font-bold text-center mb-8">繁体字练字帖生成器</h1>
      <div className="max-w-2xl mx-auto space-y-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <input 
            type="file" 
            className="w-full p-2 border rounded"
            onChange={handleImageUpload}
            accept="image/*"
          />
        </div>
        
        {selectedImage && (
          <div className="bg-white p-4 rounded-lg shadow">
            <img src={selectedImage} alt="Selected" className="max-w-full rounded" />
          </div>
        )}
        
        {isProcessing && (
          <div className="text-center text-gray-600 bg-white p-4 rounded-lg shadow">
            正在识别文字...
          </div>
        )}
        
        {recognizedText && (
          <div className="bg-white p-4 rounded-lg shadow">
            <h2 className="text-xl font-bold mb-2">识别结果：</h2>
            <textarea
              className="w-full h-48 p-2 border rounded"
              value={recognizedText}
              onChange={handleTextEdit}
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
