import React, { useState, useEffect } from 'react';
import { createWorker } from 'tesseract.js';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [recognizedText, setRecognizedText] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      await recognizeText(file);
    }
  };

  const recognizeText = async (file) => {
    setIsProcessing(true);
    try {
      const worker = await createWorker('chi_tra');
      const { data: { text } } = await worker.recognize(file);
      setRecognizedText(text);
      await worker.terminate();
    } catch (error) {
      console.error('OCR识别错误:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <h1 className="text-3xl font-bold text-center mb-8">繁体字练字帖生成器</h1>
      <div className="max-w-md mx-auto space-y-6">
        <input 
          type="file" 
          className="w-full p-2 border rounded"
          onChange={handleImageUpload}
          accept="image/*"
        />
        {selectedImage && (
          <div className="mt-4">
            <img src={selectedImage} alt="Selected" className="max-w-full rounded shadow" />
          </div>
        )}
        {isProcessing && (
          <div className="text-center text-gray-600">
            正在识别文字...
          </div>
        )}
        {recognizedText && (
          <div className="mt-4 p-4 bg-white rounded shadow">
            <h2 className="text-xl font-bold mb-2">识别结果：</h2>
            <p className="whitespace-pre-wrap">{recognizedText}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
